package com.skyorbs.features;

import com.skyorbs.biomes.BiomeType;
import com.skyorbs.core.Orb;
import org.bukkit.Material;
import org.bukkit.World;

import java.util.ArrayList;
import java.util.List;
import java.util.Random;

public class StructureGenerator {

    public static List<OreGenerator.BlockData> generateStructures(Orb orb, BiomeType biome, World world) {
        List<OreGenerator.BlockData> blocks = new ArrayList<>();
        // Use deterministic seed based on biome + planet seed for consistent structures
        Random random = new Random(orb.getSeed() + biome.name().hashCode());

        int cx = orb.getCenterX();
        int cy = orb.getCenterY();
        int cz = orb.getCenterZ();
        int radius = orb.getRadius();

        // Get biome-specific structure configuration
        StructureConfig config = getStructureConfig(biome);
        if (config == null) return blocks;

        // Configurable structure density
        double densityMultiplier = com.skyorbs.SkyOrbs.getInstance().getConfigManager().getStructureDensityMultiplier();
        int structureCount = (int)(radius * config.density * densityMultiplier);

        for (int i = 0; i < structureCount; i++) {
            // Generate positions within planet bounds
            int x = cx + random.nextInt(radius * 2) - radius;
            int z = cz + random.nextInt(radius * 2) - radius;
            int y = cy + radius; // Surface level

            // Select structure type based on biome
            StructureType type = config.getRandomType(random);

            // Generate the structure
            generateStructure(blocks, x, y, z, type, random, biome);
        }

        return blocks;
    }

    /**
     * Generate a single structure with biome-specific variations
     */
    private static void generateStructure(List<OreGenerator.BlockData> blocks, int x, int y, int z,
                                          StructureType type, Random random, BiomeType biome) {

        switch (type) {
            case DESERT_TEMPLE -> generateDesertTemple(blocks, x, y, z, random, biome);
            case JUNGLE_TEMPLE -> generateJungleTemple(blocks, x, y, z, random, biome);
            case VILLAGE_HOUSE -> generateVillageHouse(blocks, x, y, z, random, biome);
            case IGLOO -> generateIgloo(blocks, x, y, z, random, biome);
            case NETHER_FORTRESS -> generateNetherFortress(blocks, x, y, z, random, biome);
            case END_TOWER -> generateEndTower(blocks, x, y, z, random, biome);
            case CRYSTAL_SPIRE -> generateCrystalSpire(blocks, x, y, z, random, biome);
            case MUSHROOM_HUT -> generateMushroomHut(blocks, x, y, z, random, biome);
            case RUINS -> generateRuins(blocks, x, y, z, random, biome);
            case ALTAR -> generateAltar(blocks, x, y, z, random, biome);
        }
    }

    /**
     * Desert temple with biome-specific variations
     */
    private static void generateDesertTemple(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // Temel (9x9)
        for (int dx = -4; dx <= 4; dx++) {
            for (int dz = -4; dz <= 4; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y, z + dz, Material.SANDSTONE));
            }
        }

        // Duvarlar
        for (int dy = 1; dy <= 5; dy++) {
            for (int dx = -4; dx <= 4; dx++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y + dy, z - 4, Material.SANDSTONE));
                blocks.add(new OreGenerator.BlockData(x + dx, y + dy, z + 4, Material.SANDSTONE));
            }
            for (int dz = -3; dz <= 3; dz++) {
                blocks.add(new OreGenerator.BlockData(x - 4, y + dy, z + dz, Material.SANDSTONE));
                blocks.add(new OreGenerator.BlockData(x + 4, y + dy, z + dz, Material.SANDSTONE));
            }
        }

        // Ã‡atÄ±
        for (int dx = -3; dx <= 3; dx++) {
            for (int dz = -3; dz <= 3; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y + 6, z + dz, Material.ORANGE_TERRACOTTA));
            }
        }

        // Hazine odasÄ± (yeraltÄ±)
        for (int dx = -2; dx <= 2; dx++) {
            for (int dz = -2; dz <= 2; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y - 3, z + dz, Material.STONE_BRICKS));
            }
        }

        // Treasure chest with biome-specific loot
        blocks.add(new OreGenerator.BlockData(x, y - 2, z, Material.CHEST));

        // Biome-specific traps and decorations
        if (biome == BiomeType.TOXIC_SWAMP || biome == BiomeType.TOXIC) {
            // Toxic biome - poison traps
            blocks.add(new OreGenerator.BlockData(x + 1, y - 2, z + 1, Material.POISONOUS_POTATO));
            blocks.add(new OreGenerator.BlockData(x - 1, y - 2, z - 1, Material.POISONOUS_POTATO));
        } else {
            // Standard traps
            blocks.add(new OreGenerator.BlockData(x + 1, y - 2, z + 1, Material.TNT));
            blocks.add(new OreGenerator.BlockData(x - 1, y - 2, z - 1, Material.TNT));
        }
    }

    /**
     * Orman tapÄ±naÄŸÄ±
     */
    private static void generateJungleTemple(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // Piramit tabanÄ± (11x11)
        for (int dx = -5; dx <= 5; dx++) {
            for (int dz = -5; dz <= 5; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y, z + dz, Material.MOSSY_COBBLESTONE));
            }
        }

        // Piramit katlarÄ±
        for (int level = 1; level <= 6; level++) {
            int size = 6 - level;
            for (int dx = -size; dx <= size; dx++) {
                for (int dz = -size; dz <= size; dz++) {
                    Material mat = random.nextBoolean() ? Material.MOSSY_COBBLESTONE : Material.COBBLESTONE;
                    blocks.add(new OreGenerator.BlockData(x + dx, y + level, z + dz, mat));
                }
            }
        }

        // Hazine sandÄ±ÄŸÄ±
        blocks.add(new OreGenerator.BlockData(x, y + 1, z, Material.CHEST));

        // Asma yapraklar
        for (int i = 0; i < 10; i++) {
            int vineX = x + random.nextInt(11) - 5;
            int vineZ = z + random.nextInt(11) - 5;
            int vineLength = 2 + random.nextInt(4);
            for (int vl = 0; vl < vineLength; vl++) {
                blocks.add(new OreGenerator.BlockData(vineX, y + 7 - vl, vineZ, Material.VINE));
            }
        }
    }

    /**
     * KÃ¶y evi
     */
    private static void generateVillageHouse(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // Temel (7x7)
        for (int dx = -3; dx <= 3; dx++) {
            for (int dz = -3; dz <= 3; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y, z + dz, Material.OAK_PLANKS));
            }
        }

        // Duvarlar
        for (int dy = 1; dy <= 3; dy++) {
            for (int dx = -3; dx <= 3; dx++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y + dy, z - 3, Material.OAK_LOG));
                blocks.add(new OreGenerator.BlockData(x + dx, y + dy, z + 3, Material.OAK_LOG));
            }
            for (int dz = -2; dz <= 2; dz++) {
                blocks.add(new OreGenerator.BlockData(x - 3, y + dy, z + dz, Material.OAK_LOG));
                blocks.add(new OreGenerator.BlockData(x + 3, y + dy, z + dz, Material.OAK_LOG));
            }
        }

        // Ã‡atÄ±
        for (int dx = -3; dx <= 3; dx++) {
            for (int dz = -3; dz <= 3; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y + 4, z + dz, Material.OAK_STAIRS));
            }
        }

        // KapÄ±
        blocks.add(new OreGenerator.BlockData(x, y + 1, z - 3, Material.OAK_DOOR));
        blocks.add(new OreGenerator.BlockData(x, y + 2, z - 3, Material.OAK_DOOR));

        // Ä°Ã§ mobilyalar
        blocks.add(new OreGenerator.BlockData(x - 2, y + 1, z - 2, Material.CRAFTING_TABLE));
        blocks.add(new OreGenerator.BlockData(x + 2, y + 1, z + 2, Material.CHEST));
        blocks.add(new OreGenerator.BlockData(x, y + 1, z, Material.RED_BED));
    }

    /**
     * Ä°glo
     */
    private static void generateIgloo(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // Kubbe ÅŸekli (6x6 taban)
        for (int dx = -3; dx <= 3; dx++) {
            for (int dz = -3; dz <= 3; dz++) {
                double distance = Math.sqrt(dx * dx + dz * dz);
                if (distance <= 3) {
                    blocks.add(new OreGenerator.BlockData(x + dx, y, z + dz, Material.SNOW_BLOCK));

                    if (distance <= 2.5) {
                        blocks.add(new OreGenerator.BlockData(x + dx, y + 1, z + dz, Material.SNOW_BLOCK));
                    }
                    if (distance <= 1.5) {
                        blocks.add(new OreGenerator.BlockData(x + dx, y + 2, z + dz, Material.SNOW_BLOCK));
                    }
                }
            }
        }

        // GiriÅŸ
        blocks.add(new OreGenerator.BlockData(x, y + 1, z - 3, Material.AIR));

        // Ä°Ã§eride mobilya
        blocks.add(new OreGenerator.BlockData(x, y + 1, z, Material.FURNACE));
        blocks.add(new OreGenerator.BlockData(x - 1, y + 1, z + 1, Material.CHEST));
    }

    /**
     * Nether kalesi
     */
    private static void generateNetherFortress(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // Temel platform
        for (int dx = -5; dx <= 5; dx++) {
            for (int dz = -5; dz <= 5; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y, z + dz, Material.NETHER_BRICKS));
            }
        }

        // Kuleler (4 kÃ¶ÅŸe)
        int[][] corners = {{-5, -5}, {5, -5}, {-5, 5}, {5, 5}};
        for (int[] corner : corners) {
            for (int dy = 1; dy <= 8; dy++) {
                blocks.add(new OreGenerator.BlockData(x + corner[0], y + dy, z + corner[1], Material.NETHER_BRICKS));
                blocks.add(new OreGenerator.BlockData(x + corner[0] + (corner[0] > 0 ? -1 : 1), y + dy, z + corner[1], Material.NETHER_BRICKS));
                blocks.add(new OreGenerator.BlockData(x + corner[0], y + dy, z + corner[1] + (corner[1] > 0 ? -1 : 1), Material.NETHER_BRICKS));
            }
            // Kule tepesi
            blocks.add(new OreGenerator.BlockData(x + corner[0], y + 9, z + corner[1], Material.NETHER_BRICK_FENCE));
        }

        // Merkez yapÄ±
        for (int dy = 1; dy <= 5; dy++) {
            for (int dx = -2; dx <= 2; dx++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y + dy, z - 2, Material.NETHER_BRICKS));
                blocks.add(new OreGenerator.BlockData(x + dx, y + dy, z + 2, Material.NETHER_BRICKS));
            }
        }

        // Spawner
        blocks.add(new OreGenerator.BlockData(x, y + 1, z, Material.SPAWNER));

        // Lava dekorasyonu
        blocks.add(new OreGenerator.BlockData(x + 2, y + 1, z, Material.LAVA));
        blocks.add(new OreGenerator.BlockData(x - 2, y + 1, z, Material.LAVA));
    }

    /**
     * End kulesi
     */
    private static void generateEndTower(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // YÃ¼ksek kule (15 blok)
        for (int dy = 0; dy <= 15; dy++) {
            for (int dx = -2; dx <= 2; dx++) {
                for (int dz = -2; dz <= 2; dz++) {
                    if (Math.abs(dx) == 2 || Math.abs(dz) == 2) {
                        blocks.add(new OreGenerator.BlockData(x + dx, y + dy, z + dz, Material.END_STONE_BRICKS));
                    }
                }
            }
        }

        // Tepe kristali
        blocks.add(new OreGenerator.BlockData(x, y + 16, z, Material.END_ROD));
        blocks.add(new OreGenerator.BlockData(x, y + 17, z, Material.PURPUR_BLOCK));

        // Chorus bitkileri
        for (int i = 0; i < 5; i++) {
            int chX = x + random.nextInt(9) - 4;
            int chZ = z + random.nextInt(9) - 4;
            int height = 2 + random.nextInt(4);
            for (int h = 0; h < height; h++) {
                blocks.add(new OreGenerator.BlockData(chX, y + h, chZ, Material.CHORUS_PLANT));
            }
        }
    }

    /**
     * Kristal diken
     */
    private static void generateCrystalSpire(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        int height = 10 + random.nextInt(8);

        for (int dy = 0; dy < height; dy++) {
            int size = Math.max(1, 3 - dy / 3);
            for (int dx = -size; dx <= size; dx++) {
                for (int dz = -size; dz <= size; dz++) {
                    Material mat = random.nextBoolean() ? Material.AMETHYST_BLOCK : Material.BUDDING_AMETHYST;
                    blocks.add(new OreGenerator.BlockData(x + dx, y + dy, z + dz, mat));
                }
            }
        }

        // Parlayan tepe
        blocks.add(new OreGenerator.BlockData(x, y + height, z, Material.SEA_LANTERN));
    }

    /**
     * Mantar kulÃ¼besi
     */
    private static void generateMushroomHut(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // KÃ¶k
        blocks.add(new OreGenerator.BlockData(x, y, z, Material.MUSHROOM_STEM));
        blocks.add(new OreGenerator.BlockData(x, y + 1, z, Material.MUSHROOM_STEM));
        blocks.add(new OreGenerator.BlockData(x, y + 2, z, Material.MUSHROOM_STEM));

        // Åžapka
        Material capMaterial = random.nextBoolean() ? Material.RED_MUSHROOM_BLOCK : Material.BROWN_MUSHROOM_BLOCK;
        for (int dx = -2; dx <= 2; dx++) {
            for (int dz = -2; dz <= 2; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y + 3, z + dz, capMaterial));
            }
        }
    }

    /**
     * Harabeler
     */
    private static void generateRuins(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // YarÄ±m duvarlar
        for (int i = 0; i < 10; i++) {
            int rX = x + random.nextInt(9) - 4;
            int rZ = z + random.nextInt(9) - 4;
            int height = 1 + random.nextInt(3);

            Material mat = random.nextBoolean() ? Material.STONE_BRICKS : Material.CRACKED_STONE_BRICKS;
            for (int dy = 0; dy < height; dy++) {
                blocks.add(new OreGenerator.BlockData(rX, y + dy, rZ, mat));
            }
        }

        // SandÄ±k
        blocks.add(new OreGenerator.BlockData(x, y, z, Material.CHEST));
    }

    /**
     * Sunak
     */
    private static void generateAltar(List<OreGenerator.BlockData> blocks, int x, int y, int z, Random random, BiomeType biome) {
        // Platform (5x5)
        for (int dx = -2; dx <= 2; dx++) {
            for (int dz = -2; dz <= 2; dz++) {
                blocks.add(new OreGenerator.BlockData(x + dx, y, z + dz, Material.QUARTZ_BLOCK));
            }
        }

        // Merkez sÃ¼tun
        for (int dy = 1; dy <= 3; dy++) {
            blocks.add(new OreGenerator.BlockData(x, y + dy, z, Material.QUARTZ_PILLAR));
        }

        // AteÅŸ
        blocks.add(new OreGenerator.BlockData(x, y + 4, z, Material.FIRE));

        // KÃ¶ÅŸe meÅŸaleler
        blocks.add(new OreGenerator.BlockData(x - 2, y + 1, z - 2, Material.TORCH));
        blocks.add(new OreGenerator.BlockData(x + 2, y + 1, z - 2, Material.TORCH));
        blocks.add(new OreGenerator.BlockData(x - 2, y + 1, z + 2, Material.TORCH));
        blocks.add(new OreGenerator.BlockData(x + 2, y + 1, z + 2, Material.TORCH));
    }

    /**
     * Biyom bazlÄ± yapÄ± konfigÃ¼rasyonu
     */
    private static StructureConfig getStructureConfig(BiomeType biome) {
        return switch (biome) {
            case DESERT, BADLANDS -> new StructureConfig(0.015,
                new StructureType[]{StructureType.DESERT_TEMPLE, StructureType.RUINS},
                new double[]{0.7, 0.3});

            case JUNGLE -> new StructureConfig(0.012,
                new StructureType[]{StructureType.JUNGLE_TEMPLE, StructureType.RUINS},
                new double[]{0.8, 0.2});

            case PLAINS, SAVANNA, MEADOW -> new StructureConfig(0.020,
                new StructureType[]{StructureType.VILLAGE_HOUSE, StructureType.RUINS},
                new double[]{0.6, 0.4});

            case FROZEN_TUNDRA, ICE_SPIKES, GLACIER -> new StructureConfig(0.010,
                new StructureType[]{StructureType.IGLOO},
                new double[]{1.0});

            case LAVA_OCEAN, MAGMA_CAVES, OBSIDIAN_PLAINS -> new StructureConfig(0.008,
                new StructureType[]{StructureType.NETHER_FORTRESS, StructureType.RUINS},
                new double[]{0.7, 0.3});

            case VOID, CHORUS_LAND -> new StructureConfig(0.010,
                new StructureType[]{StructureType.END_TOWER},
                new double[]{1.0});

            case CRYSTAL_FOREST -> new StructureConfig(0.025,
                new StructureType[]{StructureType.CRYSTAL_SPIRE, StructureType.ALTAR},
                new double[]{0.7, 0.3});

            case MUSHROOM_GIANT -> new StructureConfig(0.030,
                new StructureType[]{StructureType.MUSHROOM_HUT},
                new double[]{1.0});

            default -> null;
        };
    }

    /**
     * YapÄ± tÃ¼rleri
     */
    private enum StructureType {
        DESERT_TEMPLE,
        JUNGLE_TEMPLE,
        VILLAGE_HOUSE,
        IGLOO,
        NETHER_FORTRESS,
        END_TOWER,
        CRYSTAL_SPIRE,
        MUSHROOM_HUT,
        RUINS,
        ALTAR
    }

    /**
     * Biyom yapÄ± konfigÃ¼rasyonu
     */
    private static class StructureConfig {
        final double density;
        final StructureType[] types;
        final double[] weights;

        StructureConfig(double density, StructureType[] types, double[] weights) {
            this.density = density;
            this.types = types;
            this.weights = weights;
        }

        StructureType getRandomType(Random random) {
            double total = 0;
            for (double w : weights) total += w;

            double rand = random.nextDouble() * total;
            double current = 0;

            for (int i = 0; i < types.length; i++) {
                current += weights[i];
                if (rand <= current) {
                    return types[i];
                }
            }

            return types[0];
        }
    }
}

src/main/java/com/skyorbs/features/Satel.content:
package com.skyorbs.features;

import com.skyorbs.SkyOrbs;
import com.skyorbs.biomes.BiomeType;
import com.skyorbs.core.Orb;
import org.bukkit.Material;
import org.bukkit.World;

import java.util.ArrayList;
import java.util.List;
import java.util.Random;
import java.util.UUID;

public class SatelliteGenerator {

    private final SkyOrbs plugin;

    public SatelliteGenerator(SkyOrbs plugin) {
        this.plugin = plugin;
    }

    /**
     * Ana gezegen iÃ§in uydular Ã¼retir
     */
    public List<Orb> generateSatellitesForPlanet(Orb planet, World world) {
        List<Orb> satellites = new ArrayList<>();
        Random random = new Random(planet.getSeed() + 777);

        int minCount = plugin.getConfigManager().getMinSatellitesPerPlanet();
        int maxCount = plugin.getConfigManager().getMaxSatellitesPerPlanet();

        // OlasÄ±lÄ±k kontrolÃ¼
        double probability = plugin.getConfigManager().getSatelliteProbability();
        if (random.nextDouble() > probability) {
            return satellites; // Uydu yok
        }

        int count = minCount + random.nextInt(maxCount - minCount + 1);

        for (int i = 0; i < count; i++) {
            Orb satellite = createSatellite(planet, world, random);
            if (satellite != null) {
                satellites.add(satellite);
            }
        }

        return satellites;
    }

    /**
     * Tek bir uydu oluÅŸturur
     */
    private Orb createSatellite(Orb parentPlanet, World world, Random random) {
        // Uydu boyutu ana gezegenden kÃ¼Ã§Ã¼k
        int parentRadius = parentPlanet.getRadius();
        int minRadius = Math.max(15, parentRadius / 4);
        int maxRadius = Math.max(25, parentRadius / 2);
        int radius = minRadius + random.nextInt(maxRadius - minRadius + 1);

        // Åžekil seÃ§imi (daha yuvarlak uydular)
        String shapeName;
        double shapeRand = random.nextDouble();
        if (shapeRand < 0.5) {
            shapeName = "SPHERE";
        } else if (shapeRand < 0.8) {
            shapeName = "ELLIPSOID";
        } else {
            shapeName = "BLOB";
        }

        // Biyom seÃ§imi (uzay temalÄ±)
        BiomeType biome = getRandomSatelliteBiome(random);

        // Pozisyon hesaplama (daha uzak)
        int[] position = calculateSatellitePosition(parentPlanet, random);
        int centerX = position[0];
        int centerY = position[1];
        int centerZ = position[2];

        // Ä°sim oluÅŸturma
        String name = "Satellite-" + UUID.randomUUID().toString().substring(0, 6);

        // Seed oluÅŸturma
        long seed = random.nextLong();

        return new Orb(
            UUID.randomUUID(),
            name,
            world.getName(),
            centerX,
            centerY,
            centerZ,
            radius,
            shapeName,
            biome.name(),
            seed,
            System.currentTimeMillis(),
            false, // isAsteroid = false (uydu)
            parentPlanet.getId()
        );
    }

    /**
     * Uydu pozisyonunu hesaplar (asteroidlerden daha uzak)
     */
    private int[] calculateSatellitePosition(Orb parentPlanet, Random random) {
        int parentX = parentPlanet.getCenterX();
        int parentY = parentPlanet.getCenterY();
        int parentZ = parentPlanet.getCenterZ();

        // Uydular daha uzak (800-1500 blok)
        int minDistance = 800;
        int maxDistance = 1500;
        int distance = minDistance + random.nextInt(maxDistance - minDistance + 1);

        // Rastgele aÃ§Ä±
        double angle = random.nextDouble() * 2 * Math.PI;

        int x = parentX + (int)(Math.cos(angle) * distance);
        int z = parentZ + (int)(Math.sin(angle) * distance);
        int y = parentY + random.nextInt(101) - 50; // Â±50 varyasyon

        return new int[]{x, y, z};
    }

    /**
     * Uydu iÃ§in uygun biyom seÃ§er
     */
    private BiomeType getRandomSatelliteBiome(Random random) {
        // Uydular iÃ§in daha egzotik biyomlar
        BiomeType[] satelliteBiomes = {
            BiomeType.CRYSTAL_FOREST,
            BiomeType.VOID,
            BiomeType.ICE_SPIKES,
            BiomeType.LAVA_OCEAN
        };

        return satelliteBiomes[random.nextInt(satelliteBiomes.length)];
    }

    /**
     * Uydu bloklarÄ±nÄ± Ã¼retir
     */
    public List<OreGenerator.BlockData> generateSatelliteBlocks(Orb satellite, BiomeType biome) {
        List<OreGenerator.BlockData> blocks = new ArrayList<>();
        Random random = new Random(satellite.getSeed());

        int cx = satellite.getCenterX();
        int cy = satellite.getCenterY();
        int cz = satellite.getCenterZ();
        int radius = satellite.getRadius();

        // Uydu ÅŸekli iÃ§in shape al
        var shape = plugin.getShapeRegistry().getShape(satellite.getShapeName());
        if (shape == null) {
            shape = plugin.getShapeRegistry().getShape("SPHERE");
        }

        // Ana bloklar
        for (int x = -radius; x <= radius; x++) {
            for (int y = -radius; y <= radius; y++) {
                for (int z = -radius; z <= radius; z++) {
                    if (shape.isBlockPart(x, y, z, radius, satellite.getSeed())) {
                        Material material = getSatelliteMaterial(biome, random);
                        blocks.add(new OreGenerator.BlockData(cx + x, cy + y, cz + z, material));
                    }
                }
            }
        }

        // Uydu Ã¶zellikleri (daha nadir)
        generateSatelliteFeatures(blocks, satellite, biome, random);

        return blocks;
    }

    /**
     * Uydu malzemesi seÃ§er
     */
    private Material getSatelliteMaterial(BiomeType biome, Random random) {
        // Biyom bazlÄ± egzotik malzemeler
        Material[] materials = switch (biome) {
            case CRYSTAL_FOREST -> new Material[]{Material.AMETHYST_BLOCK, Material.QUARTZ_BLOCK, Material.PURPUR_BLOCK};
            case VOID -> new Material[]{Material.END_STONE, Material.OBSIDIAN, Material.PURPUR_BLOCK};
            case ICE_SPIKES -> new Material[]{Material.PACKED_ICE, Material.BLUE_ICE, Material.ICE};
            case LAVA_OCEAN -> new Material[]{Material.MAGMA_BLOCK, Material.OBSIDIAN, Material.BLACKSTONE};
            default -> new Material[]{Material.STONE, Material.ANDESITE, Material.DIORITE};
        };

        return materials[random.nextInt(materials.length)];
    }

    /**
     * Uydu Ã¶zel Ã¶zellikleri Ã¼retir
     */
    private void generateSatelliteFeatures(List<OreGenerator.BlockData> blocks, Orb satellite, BiomeType biome, Random random) {
        int cx = satellite.getCenterX();
        int cy = satellite.getCenterY();
        int cz = satellite.getCenterZ();
        int radius = satellite.getRadius();

        // Ã–zel yapÄ±lar (Ã§ok nadir)
        int featureCount = Math.max(1, radius / 10);

        for (int i = 0; i < featureCount; i++) {
            int x = cx + random.nextInt(radius * 2) - radius;
            int y = cy + random.nextInt(radius * 2) - radius;
            int z = cz + random.nextInt(radius * 2) - radius;

            // Sadece uydu iÃ§inde ise
            double distance = Math.sqrt((x - cx) * (x - cx) + (y - cy) * (y - cy) + (z - cz) * (z - cz));
            if (distance <= radius - 1) {
                Material feature = getSatelliteFeature(biome, random);
                if (feature != null) {
                    blocks.add(new OreGenerator.BlockData(x, y, z, feature));
                }
            }
        }
    }

    /**
     * Uydu Ã¶zel malzemesi seÃ§er
     */
    private Material getSatelliteFeature(BiomeType biome, Random random) {
        // Ã–zel malzemeler
        Material[] features = switch (biome) {
            case CRYSTAL_FOREST -> new Material[]{Material.AMETHYST_CLUSTER, Material.SMALL_AMETHYST_BUD};
            case VOID -> new Material[]{Material.END_ROD, Material.CHORUS_PLANT};
            case ICE_SPIKES -> new Material[]{Material.BLUE_ICE, Material.PACKED_ICE};
            case LAVA_OCEAN -> new Material[]{Material.LAVA, Material.MAGMA_BLOCK};
            default -> null;
        };

        return features != null ? features[random.nextInt(features.length)] : null;
    }
}

