                chestplate.addEnchantment(Enchantment.PROTECTION, 3 + random.nextInt(2));
                loot.add(chestplate);

                // GÃ¼Ã§lÃ¼ silah
                ItemStack sword = new ItemStack(Material.DIAMOND_SWORD, 1);
