package com.skyorbs.shapes.impl;

import com.skyorbs.shapes.PlanetShape;

public class PyramidShape implements PlanetShape {

    @Override
    public boolean isBlockPart(int dx, int dy, int dz, int radius, long seed) {
        if (dy < 0 || dy > radius) {
            return false;
        }

        int levelSize = radius - dy;
        return Math.abs(dx) <= levelSize && Math.abs(dz) <= levelSize;
    }

    @Override
    public String getName() {
        return "PYRAMID";
    }

    @Override
    public double getDeformationFactor() {
        return 0.0;
    }

    @Override
    public String getDisplayName() {
        return "Piramit";
    }
}

src/main/java/com/skyorbs/shapes/impl/Sp.content:
package com.skyorbs.shapes.impl;

import com.skyorbs.shapes.PlanetShape;

public class SpikyShape implements PlanetShape {

    @Override
    public boolean isBlockPart(int dx, int dy, int dz, int radius, long seed) {
        double distance = Math.sqrt(dx * dx + dy * dy + dz * dz);

        double angle = Math.atan2(dz, dx);
        double elevation = Math.atan2(dy, Math.sqrt(dx * dx + dz * dz));

        double spikePattern = Math.sin(angle * 6) * Math.cos(elevation * 4);
        double spikeExtension = 1 + 0.3 * Math.max(0, spikePattern);

        return distance <= radius * spikeExtension;
    }

    @Override
    public String getName() {
        return "SPIKY";
    }

    @Override
    public double getDeformationFactor() {
        return 0.3;
    }

    @Override
    public String getDisplayName() {
        return "Dikenli";
    }
}

