package com.skyorbs.commands;

import com.skyorbs.SkyOrbs;
import com.skyorbs.core.Orb;
import org.bukkit.*;
import org.bukkit.command.*;
import org.bukkit.entity.Player;

import java.sql.SQLException;
import java.util.*;

public class GezegenCommand implements CommandExecutor, TabCompleter {

    private final SkyOrbs plugin;

    public GezegenCommand(SkyOrbs plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (args.length == 0) {
            sendHelp(sender);
            return true;
        }

        switch (args[0].toLowerCase()) {
            case "create", "oluÅŸtur" -> handleCreate(sender);
            case "list", "liste" -> handleList(sender);
            case "tp", "teleport", "Ä±ÅŸÄ±nlan" -> handleTeleport(sender, args);
            case "sil", "delete" -> handleDelete(sender, args);
            case "info", "bilgi" -> handleInfo(sender, args);
            case "reload", "yenile" -> handleReload(sender);
            default -> sendHelp(sender);
        }

        return true;
    }

    private void handleCreate(CommandSender sender) {
        if (!sender.hasPermission("skyorbs.create")) {
            sender.sendMessage(plugin.getConfigManager().getMessage("noPermission"));
            return;
        }

        if (!(sender instanceof Player player)) {
            sender.sendMessage(plugin.getConfigManager().getMessage("playerOnly"));
            return;
        }

        World world = player.getWorld();
        plugin.getGenerationManager().createPlanetAsync(world, player);
    }

    private void handleList(CommandSender sender) {
        try {
            List<Orb> orbs = plugin.getDatabaseManager().getAllOrbs();

            if (orbs.isEmpty()) {
                sender.sendMessage(plugin.getConfigManager().getMessageRaw("listEmpty"));
                return;
            }

            sender.sendMessage(plugin.getConfigManager().getMessageRaw("listHeader")
                .replace("{count}", String.valueOf(orbs.size())));

            for (Orb orb : orbs) {
                String shapeName = plugin.getShapeRegistry().getShape(orb.getShapeName()).getDisplayName();
                double distFromSpawn = orb.getDistanceFromSpawn();

                sender.sendMessage(String.format("Â§e%s Â§7- Â§f%s Â§7| Â§f%d,%d,%d Â§7(Spawn'dan Â§f%.0f Â§7blok)",
                    orb.getName(), shapeName, orb.getCenterX(), orb.getCenterY(), orb.getCenterZ(), distFromSpawn));
            }

        } catch (SQLException e) {
            sender.sendMessage("Â§cVeritabanÄ± hatasÄ±!");
        }
    }

    private void handleTeleport(CommandSender sender, String[] args) {
        if (!(sender instanceof Player player)) {
            sender.sendMessage(plugin.getConfigManager().getMessage("playerOnly"));
            return;
        }

        if (args.length < 2) {
            sender.sendMessage("Â§cKullanÄ±m: /gezegen tp <gezegen-ismi>");
            return;
        }

        String name = args[1];

        try {
            Orb orb = plugin.getDatabaseManager().getOrbByName(name);

            if (orb == null) {
                sender.sendMessage(plugin.getConfigManager().getMessageRaw("planetNotFound")
                    .replace("{name}", name));
                return;
            }

            World world = Bukkit.getWorld(orb.getWorldName());
            if (world == null) {
                sender.sendMessage("Â§cGezegen dÃ¼nyasÄ± bulunamadÄ±!");
                return;
            }

            Location loc = new Location(world, orb.getCenterX(), orb.getCenterY() + orb.getRadius() + 10, orb.getCenterZ());
            player.teleport(loc);

            sender.sendMessage(plugin.getConfigManager().getMessageRaw("teleported")
                .replace("{name}", orb.getName()));

        } catch (SQLException e) {
            sender.sendMessage("Â§cVeritabanÄ± hatasÄ±!");
        }
    }

    private void handleDelete(CommandSender sender, String[] args) {
        if (!sender.hasPermission("skyorbs.admin")) {
            sender.sendMessage(plugin.getConfigManager().getMessage("noPermission"));
            return;
        }

        if (args.length < 2) {
            sender.sendMessage("Â§cKullanÄ±m: /gezegen sil <gezegen-ismi>");
            return;
        }

        String name = args[1];

        try {
            Orb orb = plugin.getDatabaseManager().getOrbByName(name);

            if (orb == null) {
                sender.sendMessage(plugin.getConfigManager().getMessageRaw("planetNotFound")
                    .replace("{name}", name));
                return;
            }

            sender.sendMessage("Â§eGezegen siliniyor...");

            plugin.getGenerationManager().deletePlanet(orb)
                .thenRun(() -> {
                    sender.sendMessage(plugin.getConfigManager().getMessageRaw("planetDeleted")
                        .replace("{name}", orb.getName()));
                });

        } catch (SQLException e) {
            sender.sendMessage("Â§cVeritabanÄ± hatasÄ±!");
        }
    }

    private void handleInfo(CommandSender sender, String[] args) {
        if (args.length < 2) {
            sender.sendMessage("Â§cKullanÄ±m: /gezegen info <gezegen-ismi>");
            return;
        }

        String name = args[1];

        try {
            Orb orb = plugin.getDatabaseManager().getOrbByName(name);

            if (orb == null) {
                sender.sendMessage(plugin.getConfigManager().getMessageRaw("planetNotFound")
                    .replace("{name}", name));
                return;
            }

            List<Orb> allOrbs = plugin.getDatabaseManager().getAllOrbs();

            sender.sendMessage("Â§bâ•â•â• Gezegen Bilgileri â•â•â•");
            sender.sendMessage("Â§7Ä°sim: Â§e" + orb.getName());
            sender.sendMessage("Â§7Åžekil: Â§f" + plugin.getShapeRegistry().getShape(orb.getShapeName()).getDisplayName());
            sender.sendMessage("Â§7Biyom: Â§f" + orb.getBiomeName());
            sender.sendMessage("Â§7YarÄ±Ã§ap: Â§f" + orb.getRadius() + " blok");
            sender.sendMessage("Â§7Merkez: Â§f" + orb.getCenterX() + ", " + orb.getCenterY() + ", " + orb.getCenterZ());
            sender.sendMessage("Â§7Spawn'dan UzaklÄ±k: Â§f" + String.format("%.0f", orb.getDistanceFromSpawn()) + " blok");

            sender.sendMessage("Â§7YakÄ±ndaki Gezegenler:");
            int nearbyCount = 0;
            for (Orb other : allOrbs) {
                if (!other.getId().equals(orb.getId())) {
                    double distance = orb.getDistanceFrom(other.getCenterX(), other.getCenterZ());
                    if (distance < 3000 && nearbyCount < 5) {
                        sender.sendMessage(String.format("  Â§8â€¢ Â§e%s Â§7- Â§f%.0f blok uzakta", other.getName(), distance));
                        nearbyCount++;
                    }
                }
            }

            if (nearbyCount == 0) {
                sender.sendMessage("  Â§7(YakÄ±nda gezegen yok)");
            }

        } catch (SQLException e) {
            sender.sendMessage("Â§cVeritabanÄ± hatasÄ±!");
        }
    }

    private void handleReload(CommandSender sender) {
        if (!sender.hasPermission("skyorbs.admin")) {
            sender.sendMessage(plugin.getConfigManager().getMessage("noPermission"));
            return;
        }

        plugin.reloadPluginConfig();
        sender.sendMessage(plugin.getConfigManager().getMessage("configReloaded"));
    }

    private void sendHelp(CommandSender sender) {
        sender.sendMessage("Â§bâ•â•â• Gezegen KomutlarÄ± â•â•â•");
        sender.sendMessage("Â§e/gezegen create Â§7- Yeni gezegen oluÅŸtur");
        sender.sendMessage("Â§e/gezegen list Â§7- TÃ¼m gezegenleri listele");
        sender.sendMessage("Â§e/gezegen tp <isim> Â§7- Gezegene Ä±ÅŸÄ±nlan");
        sender.sendMessage("Â§e/gezegen info <isim> Â§7- Gezegen bilgilerini gÃ¶ster");
        sender.sendMessage("Â§e/gezegen sil <isim> Â§7- Gezegeni sil (Admin)");
        sender.sendMessage("Â§e/gezegen reload Â§7- KonfigÃ¼rasyonu yenile (Admin)");
    }

    @Override
    public List<String> onTabComplete(CommandSender sender, Command command, String alias, String[] args) {
        if (args.length == 1) {
            return Arrays.asList("create", "list", "tp", "info", "sil", "reload");
        }

        if (args.length == 2 && (args[0].equalsIgnoreCase("tp") || args[0].equalsIgnoreCase("info") || args[0].equalsIgnoreCase("sil"))) {
            try {
                List<Orb> orbs = plugin.getDatabaseManager().getAllOrbs();
                return orbs.stream().map(Orb::getName).toList();
            } catch (SQLException e) {
                return new ArrayList<>();
            }
        }

        return new ArrayList<>();
    }
}

